package reservoir

import (
	"testing"
)

func TestProofFilterAdd(t *testing.T) {
	var f ProofFilter
	f.Add([]byte{1})
	counter := 0
	for _, v := range f {
		if v == 1 {
			counter += 1
		}
	}
	const counterValue = 28
	if counterValue != counter {
		t.Fatalf("counter: %d  expected: %d", counter, counterValue)
	}
}

func checkHas(t *testing.T, f ProofFilter, data []byte) {
	if !f.Has(data) {
		t.Fatalf("filter missing: %x", data)
	}
}

func checkHasNot(t *testing.T, f ProofFilter, data []byte) {
	if f.Has(data) {
		t.Fatalf("filter has extra: %x", data)
	}
}

func TestProofFilterHas(t *testing.T) {
	var f ProofFilter
	f.Add([]byte{1})
	f.Add([]byte{3, 64, 5, 58, 41, 121, 108, 19, 182, 104, 214, 212, 71, 4, 199, 160, 206, 71, 21, 158, 19, 239, 164, 253, 228, 240, 51, 56, 246, 124, 145, 156, 132, 3, 57, 70, 242, 70, 119})
	f.Add([]byte{67, 70, 41, 170, 112, 175, 207, 53, 183, 52, 62, 0, 131, 246, 20, 119, 183, 184, 35, 39, 3, 151, 144, 241, 192, 95, 75, 33, 19, 100, 162, 36, 113, 3, 3, 234, 252, 237, 73, 205})

	checkHas(t, f, []byte{1})
	checkHas(t, f, []byte{3, 64, 5, 58, 41, 121, 108, 19, 182, 104, 214, 212, 71, 4, 199, 160, 206, 71, 21, 158, 19, 239, 164, 253, 228, 240, 51, 56, 246, 124, 145, 156, 132, 3, 57, 70, 242, 70, 119})
	checkHas(t, f, []byte{67, 70, 41, 170, 112, 175, 207, 53, 183, 52, 62, 0, 131, 246, 20, 119, 183, 184, 35, 39, 3, 151, 144, 241, 192, 95, 75, 33, 19, 100, 162, 36, 113, 3, 3, 234, 252, 237, 73, 205})
	checkHasNot(t, f, []byte{6, 76, 93, 160, 146, 63, 83, 106, 107, 163, 202, 123, 250, 224, 64, 206, 188, 213, 165, 132, 193, 243, 209, 17, 223, 163, 214, 220, 5, 5, 121, 171, 1, 228, 207, 135, 137, 230})
}

func TestProofFilterMarshal(t *testing.T) {
	var f ProofFilter
	f.Add([]byte{1})
	f.Add([]byte{3, 64, 5, 58, 41, 121, 108, 19, 182, 104, 214, 212, 71, 4, 199, 160, 206, 71, 21, 158, 19, 239, 164, 253, 228, 240, 51, 56, 246, 124, 145, 156, 132, 3, 57, 70, 242, 70, 119})
	f.Add([]byte{67, 70, 41, 170, 112, 175, 207, 53, 183, 52, 62, 0, 131, 246, 20, 119, 183, 184, 35, 39, 3, 151, 144, 241, 192, 95, 75, 33, 19, 100, 162, 36, 113, 3, 3, 234, 252, 237, 73, 205})

	b, err := f.MarshalText()
	if nil != err {
		t.Fatalf("unexpected error: %s", err)
	}

	checkLength(t, len(b), 8192)

	expected := ""
	if expected != string(b) {
		t.Fatal("strings mismatched")
	}
}
func TestProofFilterUnmarshalText(t *testing.T) {
	data := ""
	var f ProofFilter
	err := f.UnmarshalText([]byte(data))
	if nil != err {
		t.Fatalf("unexpected error: %s", err)
	}
	checkLength(t, len(f), 4096)

	checkHas(t, f, []byte{67, 70, 41, 170, 112, 175, 207, 53, 183, 52, 62, 0, 131, 246, 20, 119, 183, 184, 35, 39, 3, 151, 144, 241, 192, 95, 75, 33, 19, 100, 162, 36, 113, 3, 3, 234, 252, 237, 73, 205})
	checkHasNot(t, f, []byte{6, 76, 93, 160, 146, 63, 83, 106, 107, 163, 202, 123, 250, 224, 64, 206, 188, 213, 165, 132, 193, 243, 209, 17, 223, 163, 214, 220, 5, 5, 121, 171, 1, 228, 207, 135, 137, 230})
}

func checkLength(t *testing.T, b int, l int) {
	if b != l {
		t.Fatalf("filter length: %d  expected: %d", b, l)
	}
}
